import { Component } from "@angular/core"
import { CommonModule } from "@angular/common"
import { FormsModule } from "@angular/forms"
import { RouterModule } from "@angular/router"

@Component({
  selector: "app-contact",
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: "./contact.component.html",
  styleUrls: ["./contact.component.scss"],
})
export class ContactComponent {
  contactForm = {
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    subject: "",
    message: "",
  }

  formSubmitted = false
  formError = false

  // Office locations
  officeLocations = [
    {
      city: "Nouakchott",
      address: "123 Avenue de l'Indépendance, Tevragh Zeina",
      phone: "+222 45 25 25 25",
      email: "nouakchott@dreamhouse.mr",
      hours: "Monday - Friday: 9:00 AM - 6:00 PM\nSaturday: 10:00 AM - 4:00 PM\nSunday: Closed",
    },
    {
      city: "Nouadhibou",
      address: "45 Rue du Port, Cansado",
      phone: "+222 45 74 12 34",
      email: "nouadhibou@dreamhouse.mr",
      hours: "Monday - Friday: 9:00 AM - 5:30 PM\nSaturday: 10:00 AM - 3:00 PM\nSunday: Closed",
    },
  ]

  submitForm() {
    // Simple validation
    if (!this.contactForm.firstName || !this.contactForm.email || !this.contactForm.message) {
      this.formError = true
      return
    }

    // In a real app, you would send this data to your backend
    console.log("Form submitted:", this.contactForm)

    // Reset form and show success message
    this.formSubmitted = true
    this.formError = false
    this.contactForm = {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      subject: "",
      message: "",
    }
  }
}
