import { Component, type OnInit } from "@angular/core"
import { CommonModule } from "@angular/common"
import { RouterModule } from "@angular/router"
import type { HouseService } from "../../services/house.service"
import type { House, SearchFilters } from "../../models/house.model"
import { SearchFiltersComponent } from "../../components/search-filters/search-filters.component"
import { HouseCardComponent } from "../../components/house-card/house-card.component"

@Component({
  selector: "app-home",
  standalone: true,
  imports: [CommonModule, RouterModule, SearchFiltersComponent, HouseCardComponent],
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.scss"],
})
export class HomeComponent implements OnInit {
  featuredHouses: House[] = []
  loading = true
  currentSlide = 0

  heroSlides = [
    {
      image: "/assets/images/hero-1.jpg",
      title: "Discover Luxury Homes in Nouakchott",
      subtitle: "Exclusive properties in Mauritania's vibrant capital city",
    },
    {
      image: "/assets/images/hero-2.jpg",
      title: "Beachfront Living in Nouadhibou",
      subtitle: "Wake up to stunning ocean views every day",
    },
    {
      image: "/assets/images/hero-3.jpg",
      title: "Traditional Charm in Kiffa",
      subtitle: "Experience authentic Mauritanian architecture and culture",
    },
  ]

  constructor(private houseService: HouseService) {}

  ngOnInit() {
    this.loadFeaturedHouses()
    this.startHeroSlideshow()
  }

  loadFeaturedHouses() {
    this.houseService.getFeaturedHouses().subscribe({
      next: (houses) => {
        this.featuredHouses = houses.slice(0, 6) // Show only 6 featured houses
        this.loading = false
      },
      error: (error) => {
        console.error("Error loading featured houses:", error)
        this.loading = false
      },
    })
  }

  onFiltersChanged(filters: SearchFilters) {
    // Navigate to houses page with filters
    // This would typically use Router with query params
    console.log("Filters changed:", filters)
  }

  startHeroSlideshow() {
    setInterval(() => {
      this.currentSlide = (this.currentSlide + 1) % this.heroSlides.length
    }, 5000)
  }

  goToSlide(index: number) {
    this.currentSlide = index
  }

  get currentHeroSlide() {
    return this.heroSlides[this.currentSlide]
  }
}
