import { Component, type OnInit } from "@angular/core"
import { CommonModule } from "@angular/common"
import { type ActivatedRoute, RouterModule } from "@angular/router"
import type { HouseService } from "../../services/house.service"
import type { House } from "../../models/house.model"

@Component({
  selector: "app-house-detail",
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: "./house-detail.component.html",
  styleUrls: ["./house-detail.component.scss"],
})
export class HouseDetailComponent implements OnInit {
  house: House | null = null
  loading = true
  error = false
  currentImageIndex = 0
  isFavorite = false

  constructor(
    private route: ActivatedRoute,
    private houseService: HouseService,
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      const id = +params["id"]
      this.loadHouse(id)
    })
  }

  loadHouse(id: number): void {
    this.loading = true
    this.houseService.getHouseById(id).subscribe({
      next: (house) => {
        this.house = house
        this.loading = false
      },
      error: (err) => {
        console.error("Error loading house:", err)
        this.error = true
        this.loading = false
      },
    })
  }

  nextImage(): void {
    if (this.house && this.house.imageUrls.length > 0) {
      this.currentImageIndex = (this.currentImageIndex + 1) % this.house.imageUrls.length
    }
  }

  prevImage(): void {
    if (this.house && this.house.imageUrls.length > 0) {
      this.currentImageIndex = (this.currentImageIndex - 1 + this.house.imageUrls.length) % this.house.imageUrls.length
    }
  }

  toggleFavorite(): void {
    this.isFavorite = !this.isFavorite
    // TODO: Implement favorite functionality with backend
  }

  formatPrice(price: number): string {
    return `${(price / 1000000).toFixed(1)}M MRU`
  }
}
